mapper.select:
select id, name, password, auth, sessionkey, sessionlimit, section_fullcode 
from user 
where id = #{id} 
and password = #{password}
and section_fullcode = #{section_fullcode}

mapper.update:
update user set password = #{newPassword}
where id = #{id} 
and password = #{password}

