mapper.select:
select section_name, section_pass 
from com_section
where section_cd = #{section_cd}

mapper.update:
update com_section set
section_name = #{section_name},
section_pass = #{section_pass}
where
section_cd = #{section_cd}

mapper.select:

SELECT section_name, section_pass  
FROM com_section 
WHERE bureau_cd = #{bureau_cd} 
AND section_cd= #{section_cd} 
AND section_use =#{section_use}
