mapper.select:
select st_no, real_use_dep_nm, posit_nm, adi_info7, telno, email_addr, img_filenm, st_sort, st_status 
from com_staff2
where st_no = #{st_no}


SELECT st_no, real_use_dep_nm, posit_nm, adi_info7, telno, email_addr, img_filenm, st_sort, st_status 
FROM com_staff2 
WHERE section_cd= #{section_cd} 
AND usr_work_state_code_nm = "재직"  
AND team_cd = #{team_cd}
ORDER BY st_sort, team_cd
