mapper.select:
select st_no, st_nm, st_position, st_job, st_tel, st_email, img_filenm, st_sort, st_status 
from com_staff
where st_no = #{st_no}


SELECT st_no, st_nm, st_position, st_job, st_tel, st_email, img_filenm, st_sort, st_status
FROM com_staff 
WHERE section_cd= #{section_cd} 
AND st_display = #{st_display}  
AND team_cd = #{team_cd}  
ORDER BY st_sort
